import React from 'react'
import css from "../components/menu.css"
import json from "../data/menuItems.json"



class Items extends React.Component{
    render() {
        return <li>
            { this.props.name }
          { this.props.children }
      </li>
    }
}

class Item extends React.Component {
	render() {
  	return <li>
      	{ this.props.name }
        { this.props.children }
    </li>
  }
}

class List extends React.Component {
	constructor() {
  	super();
  }
  
  list(data) {
  	const children = (items) => {
    	if (items) {
      	return <ul>{ this.list(items) }</ul>
      }
    }
    
    return data.map((node, index) => {
      return <Item key={ node.id } name={ node.name }>
        { children(node.items) }
      </Item>
    })
  }
  
  render() {
  	return <ul>
    	{ this.list(this.props.data) }
    </ul>
  }
}


const data = [{
	"id": 1,
  "name": "Hello Kitty",
  "items": [{
  	"id": 2, 
    "name": "Kitty Muu Muu"
  }, {
  	"id": 3,
    "name": "Kitty smack"
  }]
}, {
	"id": 4,
  "name": "Hello Pussy",
  "items": [{
    "id": 5,
    "name": "World",
    "items": [{
    	"id": 6,
      "name": "Hello Pussy world"
    }]
  }]
}];


export default Menu

